import React, {useCallback} from 'react';
import {ITodo} from '../interface';

interface TodoListProps {
    todos: ITodo[],
    onToggle(id: number): void
}

export const TodoList: React.FC<TodoListProps> = ({ todos, onToggle }) => {

    const toggleHendler = useCallback((id: number) => onToggle(id), []);

    return(
        <ul>
            {todos.map(todo =>
                <li className={`todo ${todo.completed ? "completed" : ""}`} key={todo.id}>
                    <label>
                        <input type="checkbox" onClick={toggleHendler}/>
                        <span>{todo.title}</span>
                        <i className="material-icons red-text">delete</i>
                    </label>
                </li>
            )}
        </ul>
    )
};
